import React from "react";
import "./Rentals.css";
import { Link } from "react-router-dom";
import  { useLocation } from "react-router";
import { Button, ConnectButton, Icon, useNotification } from "web3uikit";
import logo from "../images/airbnbRed.png";
import RentalsMap from "../components/RentalsMap";
import { useState, useEffect } from "react";
import { useMoralis, useWeb3ExecuteFunction } from "react-moralis";

const Rentals = () => {

  const {state: searchFilters} = useLocation();
  const [highLight, setHighLight]=useState();
  const {Moralis}= useMoralis();
  const [rentalsList, setRentalsList]=useState();
  const [coOrdinates, setCoOrdinates]=useState();
  const contractProcessor = useWeb3ExecuteFunction();
  const dispatch = useNotification();

  const handleSuccess=() =>{
    dispatch({
      type: "success",
      message: `Nice! You are going to ${searchFilters.destination}!!`,
      title: "Booking Succesful",
      position: "topL",
    });
  };
  const handleError=(msg) =>{
    dispatch({
      type: "error",
      message: `${msg}`,
      title: "Booking Failed",
      position: "topL",
    });
  };



    useEffect(()=>{
       async function fetchRentalsList() {
         const Rentals= Moralis.Object.extend("Rental");
         const query= new Moralis.Query(Rentals);
         query.equalTo("city", searchFilters.destination);
         query.greaterThanOrEqualTo("maxGuests_decimal", searchFilters.guests);

         const result= await query.find();
         let cords=[];
         result.forEach((e)=>{
         cords.push({ lat: e.attributes.lat, lng: e.attributes.long});
         });

         setCoOrdinates(cords);
         setRentalsList(result);
       }
       fetchRentalsList()
    }, [searchFilters])

    const bookRental = async function (start, end, id, dayPrice){

      for(
        var arr=[], dt= new Date(start);
        dt<=end;
        dt.setDate(dt.getDate() +1)
      ) {
        arr.push(new Date(dt).toISOString().slice(0,10)); //yyyy-mm-dd
      }

      let options={
        contractAddress: "0x122fE0D6fB7dD6e4d87c54E8DC475568449d4D7A",
        functionName: "addDatesBooked",
        abi: [
          {
            "inputs": [
              {
                "internalType": "uint256",
                "name": "id",
                "type": "uint256"
              },
              {
                "internalType": "string[]",
                "name": "newBookings",
                "type": "string[]"
              }
            ],
            "name": "addDatesBooked",
            "outputs": [],
            "stateMutability": "payable",
            "type": "function"
          }
        ], 
        params: {
          id: id,
          newBookings: arr,
        },
        msgValue: Moralis.Units.ETH(dayPrice * arr.length),
      }

      await contractProcessor.fetch({
        params: options,
        onSuccess: ()=>{
          handleSuccess();
        },
        onError: (error) => {
          handleError(error.data.message)
        }
      });

    }








  return (
    <>
     <div className="topBanner">
       <div>
         <Link to="/">
           <img className="logo" src={logo} alt="logo"></img>
         </Link>
       </div>
       <div className="searchReminder">

         <div className="filter">
           {searchFilters.destination}
         </div>
         <div className="vl" />
         <div className="filter">
           {`
             ${searchFilters.checkIn.toLocaleString("default",{month: "short",})}
             ${searchFilters.checkIn.toLocaleString("default",{day: "2-digit",})}
             -
             ${searchFilters.checkOut.toLocaleString("default",{month: "short",})}
             ${searchFilters.checkOut.toLocaleString("default",{day: "2-digit",})}
           `}
         </div>
         <div className="vl" />
         <div className="filter">
           {searchFilters.guests} Guest
         </div>
         <div className="searchFiltersIcon">
            <Icon fill="#ffffff" size={20} svg="search" />
          </div>
       </div>
       <div className="lrContainers">
         <ConnectButton />
       </div>
     </div>

     <hr className="line" />
     <div className="rentalsContent">
       <div className="rentalsContentL">
         Stays Available For Your Destination
         {rentalsList &&
         rentalsList.map((e,i) =>{
           return(
             <>
             <hr className="line2" />
             <div className={highLight ==i ?"rentalDivH" : "rentalDiv"}>
               <img className="rentalImg" src={e.attributes.imgUrl}></img>
               <div className="rentalInfo">
                 <div className="rentalTitle">{e.attributes.name}</div>
                 <div className="rentalDesc">
                   {e.attributes.unoDescription}
                 </div>
                 <div className="rentalDesc">
                   {e.attributes.dosDescription}
                 </div>
                 <div className="bottomButton">
                   <Button
                   onClick={()=>{
                     if(account){
                       bookRental(
                         searchFilters.checkIn,
                         searchFilters.checkOut,
                         e.attributes.uid_decimal.value.$
                       )
                     }
                   }}
                    text="Stay Here"
                    />
                  <div className="price">
                    <Icon fill="#808080" size={10} svg="matic" />{e.attributes.pricePerDay}/Day
                  </div>
                 </div>
               </div>
             </div>
             </>

           )
         })}
       </div>
       <div className="rentalsContentR">
         <RentalsMap locations={coOrdinates} setHighLight={setHighLight}/>
       </div>
     </div>
    </>
  );
};

export default Rentals;
